import React, {Component} from 'react';
import Img from 'react-image';

class Home extends Component {
    render() {
        const divStyle = {
            backgroundImage: "./sky-space.jpg"
        };
        function changeColor(event)
        {
            console.log(event.target);
            var element=this.refs.spanText;
            var domNode = ReactDOM.findDOMNode(this.span);
        };
        function getRandomColor() {
            var letters = '0123456789ABCDEF';
            var color = '#';
            for (var i = 0; i < 6; i++) {
                color += letters[Math.floor(Math.random() * 16)];
            }
            return color;
        };
        return (
            <div style={divStyle}>
                <div>
                    <Img src={'./profile.jpg'} width="128px"/>
                    <h2>Alper Tunca</h2>
                    <h3>Student Developer</h3>
                </div>

                <div className="container content">
                    <h4>
                        <span className="title">Hi, I'm Alper Tunca  </span>
                        <i class="far fa-hand-paper fa-1x"/>
                    </h4>

                    <p>
                        <span id="spanText"  style={{color : "blue"}} onClick={changeColor.bind(this)}>
                            I'm a senior in electrical and electronics engineering.
                            I am trying to prove myself by learning many programming languages.
                            I participate in many events and competitions alone or with my team. <br/>
                            I develop projects mostly with JavaScript using React, Node and with Java using Android.
                            I also design basic circuit boards.
                        </span>

                    </p>
                </div>


            </div>
        );
    }
}

export default Home;
