import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
            border: props.border,
            info: props.info,
            link: props.link
        };
    }

    render() {
        return (
            <div>
                <Card border={this.state.border} style={{background: "#2B2A28"}}>
                    <Card.Header style={{color: "#34A4EB"}}> {this.state.cardHeader}</Card.Header>
                    <Card.Body className="mt-2 mb-2" style={{display: "flex", flexDirection: "row"}}>
                        <Card.Img variant="left" src={this.state.imgURL} style={{width: "140px", height: "140px"}}/>
                        <Card.Text className="ml-4" style={{color: "#FFFFFF", textAlign: "left"}}>
                            {this.state.info}
                        </Card.Text>
                    </Card.Body>
                    <Button variant="primary">Go to Project</Button>
                </Card>
            </div>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string,
    border: PropTypes.string,
    info: PropTypes.string,
    link: PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
    border: "primary",
    info: "Add Info for this Project",
    link: "_blank"
};


export default ProjectCard;
