import React from 'react';
import './App.css';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { Link } from "react-router-dom";
import Projects from './pages/projects';
import Home from './pages/home';
import Skills from "./pages/skills";

/*
 Projects sayfasını tamamla
 home ve projects linklerini bağla
 hi everyone yazısı default değerini alamıyor ona bak
 imageler resize edilecek
 */

function App() {

    const textStyle = {
        color: "#34A4EB",
        fontSize: "16px"
    };

    const activeStyle = {
        fontWeight: "bold",
        color: "red"
    };

    return (
        <div className="App bg">

            <Navbar bg="dark" expand="lg">
                <Navbar.Brand className="ml-2" style={{color: "#34A4EB", fontSize: "20px"}}>Alper Tunca</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav mr-1" style={{backgroundColor: "#34A4EB"}}/>
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav activeKey="/home" className="ml-auto mr-2">
                        <Nav.Link href="/home" style={textStyle} activeStyle={activeStyle} >Home</Nav.Link>
                        <Nav.Link href="/projects" style={textStyle} activeStyle={activeStyle} >Projects</Nav.Link>
                        <Nav.Link href="/skills" style={textStyle} activeStyle={activeStyle} >Skills</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

        </div>
    );
}

export default App;
