import React, {Component} from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
            border: props.border,
            info: props.info,
            link: props.link
        };
    }

    openInNewTab(url) {
        var win = window.open(url, '_blank');
        win.focus();
    }

    render() {
        return (
            <div className="m-4" style={{maxWidth: "40%", width: "40%"}}>
                <Card border={this.state.border} style={{background: "#2B2A28", cursor: "pointer"}}
                      onClick={this.openInNewTab.bind(this, this.state.link)}>


                    <Card.Header style={{color: "#34A4EB" }}>
                        <Card.Img className="mr-4" src={this.state.imgURL} style={{
                            minWidth: "95px",maxWidth: "120px",width: "120px",maxHeight: "95px",height: "95px", align : "rifght"}}/>
                        {this.state.cardHeader}
                    </Card.Header>

                    <Card.Body className="mt-2 mb-2" style={{display: "flex"}}>
                        <Card.Text className="ml-3 has-text-light"
                                   style={{color: "#FFFFFF", textAlign: "left",}}>
                            {this.state.info}
                        </Card.Text>
                    </Card.Body>

                </Card>
            </div>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string,
    border: PropTypes.string,
    info: PropTypes.string,
    link: PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
    border: "primary",
    info: "Add Info for this Project",
    link: "_blank"
};


export default ProjectCard;
