import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
            border : props.border
        };
    }

    render() {
        return (
            <div>

                <Card border={this.state.border+"2px"} style={{width: '60rem'}}>
                    <Card.Header> {this.state.cardHeader}</Card.Header>
                    <Card.Body style = {{display:"flex", flexDirection:"row"}}>
                        <Card.Img variant="left" src={this.state.imgURL} style={{width: "140px", height: "140px"}}/>
                        <Card.Text>
                            Some quick example text to build on the card title and make up the bulk of
                            the card's content.
                        </Card.Text>
                    </Card.Body>
                    <Button variant="primary">Go to Project</Button>
                </Card>
            </div>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string,
    border : PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
    border : "primary"
};


export default ProjectCard;
