import React, {Component} from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
            border: props.border,
            info: props.info,
            link: props.link
        };
    }

    openInNewTab(url) {
        var win = window.open(url, '_blank');
        win.focus();
    }

    render() {
        return (
            <div className="m-4 col-md-6 col-xs-1">
                <Card border={this.state.border} style={{background: "#2B2A28", cursor: "pointer", maxHeight : "60%", height : "60%"}}
                      onClick={this.openInNewTab.bind(this, this.state.link)}>
                    <Card.Header style={{color: "#34A4EB",alignText : "center"}}>
                            {this.state.cardHeader}
                    </Card.Header>
                    <Card.Body className="mt-2 mb-2" style={{display: "flex", maxHeight : "20%", }}>
                        <Card.Img className="col-xs-0 col-md-2" src={this.state.imgURL} style={{
                            width : "110px", height: "100px", minWidth : "110px", display : "flex", align : "center", float : "left"}}/>
                        <div className="">
                            <Card.Text className="ml-1"
                                       style={{color: "#FFFFFF", textAlign: "left", overflow : "auto", margin : "auto"}}>
                                {this.state.info}
                            </Card.Text>
                        </div>
                    </Card.Body>
                </Card>
            </div>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string,
    border: PropTypes.string,
    info: PropTypes.string,
    link: PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
    border: "primary",
    info: "Add Info for this Project",
    link: "_blank"
};


export default ProjectCard;
