import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
        };
    }

    render() {
        return (
            <div>
                <Card style={{width: '60rem',display:"flex", flexDirection:"row"}}>
                    <Card.Header> {this.state.cardHeader}</Card.Header>
                    <Card.Img className="ml-2 mt-2" variant="left" src={this.state.imgURL} style={{width: "140px", height: "140px"}}/>
                    <Card.Body>
                        <Card.Text>
                            Some quick example text to build on the card title and make up the bulk of
                            the card's content.
                        </Card.Text>
                        <Button variant="primary">Go somewhere</Button>
                    </Card.Body>
                </Card>
            </div>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
};


export default ProjectCard;
