import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Image from "react-bootstrap/Image";
import Col from "react-bootstrap/Col";
import { Helmet } from 'react-helmet'

const title = "Alper Tunca";

class Home extends Component {
    render() {
        const divStyle = {
            width: "100%",
            height: "1080px",
            background :`url(${"sky-space.jpg"})`

        };

        const iStyle = {
                color : "#34A4EB"
        };

        function changeColor(event)
        {
            console.log(event.target);
            var domNode = ReactDOM.findDOMNode(this.spanText);
            var color = getRandomColor();
        };

        function getRandomColor() {
            var letters = '0123456789ABCDEF';
            var color = '#';
            for (var i = 0; i < 6; i++) {
                color += letters[Math.floor(Math.random() * 16)];
            }
            return color;
        };

        return (
            <div style={divStyle}>
                <Helmet>
                    <title>{title}</title>
                </Helmet>
                <div className="container content">
                    <Col>
                        <Image className="mt-3 " src={'./profile-photo.jpg'} width="128px" roundedCircle />
                    </Col>
                    <h2 style={{color : "#34A4EB", fontSize : "35px"}}>Alper Tunca</h2>
                    <h3 style={{color : "#B51030", fontSize : "30px"}}>Engineer & Developer</h3>

                    <p>
                        <i className="fab fa-linkedin fa-2x mr-3 " style={iStyle}/>
                        <i className="fab fa-twitter-square fa-2x mr-3 " style={iStyle}/>
                        <i className="fab fa-github-square fa-2x" style={iStyle}/>
                    </p>
                </div>
                <br/>
                <br/>
                <div className="container content">
                    <h4>
                        <span id="spanText" ref="spanText" className="title"  onClick={changeColor.bind(this)} style={iStyle}>Hi, Everyone  </span>
                        <span className="emoji" role="img" aria-label="wave">👋</span>
                    </h4>

                    <p>
                        <span style={{ color : "#34A4EB",fontSize: "20px"}}>
                            I'm a student in electrical and electronics engineering. I am trying
                            to prove myself by learning many programming languages. I participate
                            in many events and competitions alone or with my team. <br/>
                            I develop projects mostly with JavaScript using React, Node and with Java using Android.
                            I also design basic circuit boards.
                        </span>
                    </p>


                    <p>
                        {`I'm a Web Developer, I work with Front-end creating apps and web pages.
                        I participate in events about technology and I like to study productivity and communication, it helps me stay organized and collaborative.`}
                    </p>
                    <p>
                        I develop projects mostly with JavaScript using React and Node.
                    </p>


                </div>


            </div>
        );
    }
}

export default Home;
