import React, {Component} from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";
import {Media} from "react-bootstrap";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
            border: props.border,
            info: props.info,
            link: props.link
        };
    }

    openInNewTab(url) {
        var win = window.open(url, '_blank');
        win.focus();
    }

    render() {
        return (
            <div className="m-4" style={{maxWidth: "40%"}}>
                <Card border={this.state.border} style={{background: "#2B2A28", cursor: "pointer"}}
                      onClick={this.openInNewTab.bind(this, this.state.link)}>


                    <Card.Header style={{color: "#34A4EB",alignText : "center"}}>
                            {this.state.cardHeader}
                    </Card.Header>

                    <div className="card-content">
                        <Media>
                            <Media className="left">

                                <Card.Img className="m-2 mb-2" src={this.state.imgURL} style={{
                                    width : "95px",height: "95px",minWidth : "95px", display : "flex", align : "center", float : "left"}}/>
                            </Media>
                            <Media className="right">
                                <Card.Text className="m-2"
                                           style={{color: "#FFFFFF", textAlign: "left"}}>
                                    {this.state.info}
                                </Card.Text>
                            </Media>

                        </Media>

                    </div>

                </Card>
            </div>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string,
    border: PropTypes.string,
    info: PropTypes.string,
    link: PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
    border: "primary",
    info: "Add Info for this Project",
    link: "_blank"
};


export default ProjectCard;
