import React, {Component} from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from "prop-types";
import Home from "../pages/home";


class ProjectCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cardHeader: props.cardHeader,
            imgURL: props.imgURL,
            border: props.border,
            info: props.info,
            link: props.link
        };
    }

    openInNewTab(url) {
        var win = window.open(url, '_blank');
        win.focus();
    }

    render() {
        return (
                <Card className="mt-3" border={this.state.border} style={{background: "#2B2A28", cursor: "pointer"}}
                      onClick={this.openInNewTab.bind(this, this.state.link)}>
                    <Card.Header style={{color: "#34A4EB"}}> {this.state.cardHeader}</Card.Header>
                    <Card.Body className="mt-2 mb-2 media-content" style={{display: "flex", flexDirection: "row"}}>
                        <Card.Img src={this.state.imgURL} style={{maxWidth: "140px",width: "140px", maxHeight: "140px", height: "140px"}}/>
                        <Card.Text className="ml-4" style={{color: "#FFFFFF", textAlign: "left"}}>
                            {this.state.info}
                        </Card.Text>
                    </Card.Body>
                </Card>
        );
    }
}

Home.propTypes = {
    cardHeader: PropTypes.string,
    imgURL: PropTypes.string,
    border: PropTypes.string,
    info: PropTypes.string,
    link: PropTypes.string
};

Home.defaultProps = {
    cardHeader: 'Header Project',
    border: "primary",
    info: "Add Info for this Project",
    link: "_blank"
};


export default ProjectCard;
